mapper.select:



        select nno, ntitle, nwriter_id, ncontent, nregdate, updatedate,nhit
        from(
        		   select /*+INDEX_DESC(anotice nno) */
                 rownum rnum, nno, ntitle, nwriter_id, ncontent, nregdate, updatedate,nhit
                from anotice 
                where 
               rownum <= #{pageNum} * #{amount}
		order by nno) 
    		   where rnum > (#{pageNum} -1) * #{amount}
		order by nno
